import React, { Component } from 'react';
import {Input, List, Grid, Image, Button, Icon } from 'semantic-ui-react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';


class Recipe extends Component {
  constructor(props){
  super(props);
  this.state={
    posts: [],
    data: [],
    categoryId:'',
    title:'',
    text:''
   }

  }

  fetchList_2 = () => {
     var  value = this.props.recipeId;
      fetch('https://test-task-server.herokuapp.com/api/v1/recipe/item/'+value)
            .then((res) => { return res.json() })
            .then((data) => {
                console.log(data);
                this.setState({ data });
                });
          }
  componentDidMount(){
    this.fetchList_2();
  }

  render(){
let listOfOneCategory = this.state.data;

    return(
<Router>
<div style={{marginLeft:500}}>
      {listOfOneCategory.map((el,index)=>{return <li key={index}>
      <h2 >{el.title}</h2><br/>
      <span style={{width:350}}>{el.text}</span><br/>

          </li>}) }


   </div>
</Router>);
  }
}

export default Recipe;
